import { useEffect, useRef, useState } from 'react';
import * as PIXI from 'pixi.js';
import Bullet from './bullet';
import Rope from './rope';

const Character = ({ app }) => {
  const characterRef = useRef(null);
  const [keysPressed, setKeysPressed] = useState({});
  const [bullets, setBullets] = useState([]);
  const [showRope, setShowRope] = useState(false);

  useEffect(() => {
    app.loader.add('characterImage', 'path_to_your_image.png');
    app.loader.load((loader, resources) => {
      const character = new PIXI.Sprite(resources.characterImage.texture);
      characterRef.current = character;

      character.x = app.screen.width / 2;
      character.y = app.screen.height / 2;

      app.stage.addChild(character);
    });

    const handleKeyDown = (e) => {
      setKeysPressed(keysPressed => ({ ...keysPressed, [e.key]: true }));

      if (e.key === 'g') {
        setBullets(bullets => [
          ...bullets,
          <Bullet key={bullets.length} app={app} startX={characterRef.current.x + 50} startY={characterRef.current.y + 25} />
        ]);
      }

      if (e.key === ' ') { // Space key
        setShowRope(true);
      }
    };

    const handleKeyUp = (e) => {
      setKeysPressed(keysPressed => ({ ...keysPressed, [e.key]: false }));

      if (e.key === ' ') { // Space key
        setShowRope(false);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [app]);

  useEffect(() => {
    const moveCharacter = () => {
      if (keysPressed.w && characterRef.current) characterRef.current.y -= 5;
      if (keysPressed.a && characterRef.current) characterRef.current.x -= 5;
      if (keysPressed.s && characterRef.current) characterRef.current.y += 5;
      if (keysPressed.d && characterRef.current) characterRef.current.x += 5;
    };

    app.ticker.add(moveCharacter);

    return () => {
      app.ticker.remove(moveCharacter);
    };
  }, [app, keysPressed]);

  return (
    <>
      {bullets}
      {showRope && characterRef.current && <Rope app={app} character={characterRef.current} />}
    </>
  );
};

export default Character;
