import React, { useEffect } from 'react';
import Character from './Character';
import * as PIXI from 'pixi.js';

const Game = ({ app }) => {
  useEffect(() => {
    // Create the ground
    const ground = new PIXI.Graphics();
    ground.beginFill(0x008000); // green color
    ground.drawRect(0, app.screen.height - 50, app.screen.width, 50); // draw a rectangle
    ground.endFill();

    // Add the ground to the stage
    app.stage.addChild(ground);

    // Continuously move the ground to the left
    app.ticker.add(() => {
      ground.x -= 2;
      if (ground.x + ground.width < 0) {
        ground.x = 0;
      }
    });
  }, [app]);

  return <Character app={app} />;
};

export default Game;
