import React, { useEffect, useRef, useState } from 'react';
import * as PIXI from 'pixi.js';
import { Stage, Sprite } from 'react-pixi';

const Character = ({ app }) => {
  const characterRef = useRef(null);
  const [keysPressed, setKeysPressed] = useState({});

  useEffect(() => {
    const character = new PIXI.Graphics();
    character.beginFill(0xff0000); // red color
    character.drawRect(0, 0, 50, 50); // draw a rectangle
    character.endFill();

    character.x = app.screen.width / 2;
    character.y = app.screen.height / 2;

    app.stage.addChild(character);
    characterRef.current = character;

    const handleKeyDown = (e) => {
      setKeysPressed((keysPressed) => ({ ...keysPressed, [e.key]: true }));
    };

    const handleKeyUp = (e) => {
      setKeysPressed((keysPressed) => ({ ...keysPressed, [e.key]: false }));
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [app]);

  useEffect(() => {
    const character = characterRef.current;
    const moveCharacter = () => {
      if (keysPressed.w) character.y -= 5;
      if (keysPressed.a) character.x -= 5;
      if (keysPressed.s) character.y += 5;
      if (keysPressed.d) character.x += 5;
    };

    app.ticker.add(moveCharacter);

    return () => {
      app.ticker.remove(moveCharacter);
    };
  }, [app, keysPressed]);

  return <Stage />;
};

export default Character;
