import { useEffect, useRef, useState } from 'react';
import * as PIXI from 'pixi.js';
import Bullet from './Bullet';

const Character = ({ app }) => {
  const characterRef = useRef();
  const [keysPressed, setKeysPressed] = useState({});
  const [bullets, setBullets] = useState([]);

  useEffect(() => {
    const loader = PIXI.Loader.shared;

    loader.add('characterImage', 'path_to_your_image.png').load((loader, resources) => {
      const character = new PIXI.Sprite(resources.characterImage.texture);
      characterRef.current = character;

      character.x = app.screen.width / 2;
      character.y = app.screen.height / 2;

      app.stage.addChild(character);
    });

    const handleKeyDown = (e) => {
      setKeysPressed(keysPressed => ({ ...keysPressed, [e.key]: true }));

      if (e.key === 'g') {
        setBullets(bullets => [
          ...bullets,
          <Bullet key={bullets.length} app={app} startX={characterRef.current.x + 50} startY={characterRef.current.y + 25} />
        ]);
      }
    };

    const handleKeyUp = (e) => {
      setKeysPressed(keysPressed => ({ ...keysPressed, [e.key]: false }));
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [app]);

  useEffect(() => {
    const moveCharacter = () => {
      if (!characterRef.current) return;

      if (keysPressed.w) characterRef.current.y -= 5;
      if (keysPressed.a) characterRef.current.x -= 5;
      if (keysPressed.s) characterRef.current.y += 5;
      if (keysPressed.d) characterRef.current.x += 5;
    };

    app.ticker.add(moveCharacter);

    return () => {
      app.ticker.remove(moveCharacter);
    };
  }, [app, keysPressed]);

  return (
    <>
      {bullets}
    </>
  );
};

export default Character;
